jit(false);

function test()
{
  enterFunc ('test');
  printBugNumber(BUGNUMBER);
  printStatus (summary);


  var testPairs = [
    ["{}", {}],
    ["[]", []],
    ['{"foo":"bar"}', {"foo":"bar"}],
    ['{"null":null}', {"null":null}],
    ['{"five":5}', {"five":5}],
  ]
  
  var a = [];
  for (var i=0; i < testPairs.length; i++) {
    var pair = testPairs[i];
    var s = emulatedJSON.stringify(pair[1])
    a[i] = s;
  }
  print(a.join("\n"));
 
  reportCompare(expect, actual, summary);

  exitFunc ('test');
}

